import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { MdOutlineHowToReg, MdOutlineAnalytics, MdOutlineSmartphone, MdOutlineSupportAgent } from 'react-icons/md';
import './LandingPage.css';

const LandingPage = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [email, setEmail] = useState('');

  const handleNewsletterSubmit = (e) => {
    e.preventDefault();
    console.log('Newsletter subscription for:', email);
    setEmail('');
  };

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    const navbarHeight = document.querySelector('.navbar').offsetHeight;
    const elementPosition = element.getBoundingClientRect().top;
    const offsetPosition = elementPosition + window.pageYOffset - navbarHeight;

    window.scrollTo({
      top: offsetPosition,
      behavior: "smooth"
    });
  };

  return (
    <div className="landing-container">
      <section id="features" className="features-section">
        <h2>Smart Features for Smart Education</h2>
        <div className="features-grid">
          <div className="feature-card">
            <div className="feature-content">
              <div className="feature-icon">
                <MdOutlineHowToReg />
              </div>
              <h3>Smart Attendance</h3>
              <p>AI-powered face recognition for seamless attendance tracking</p>
            </div>
            <div className="feature-hover"></div>
          </div>
          <div className="feature-card">
            <div className="feature-content">
              <div className="feature-icon">
                <MdOutlineAnalytics />
              </div>
              <h3>Real-time Analytics</h3>
              <p>Comprehensive insights into academic performance</p>
            </div>
            <div className="feature-hover"></div>
          </div>
          <div className="feature-card">
            <div className="feature-content">
              <div className="feature-icon">
                <MdOutlineSmartphone />
              </div>
              <h3>Mobile Access</h3>
              <p>Access your campus anywhere, anytime</p>
            </div>
            <div className="feature-hover"></div>
          </div>
          <div className="feature-card">
            <div className="feature-content">
              <div className="feature-icon">
                <MdOutlineSupportAgent />
              </div>
              <h3>AI Assistant</h3>
              <p>24/7 support for students and faculty</p>
            </div>
            <div className="feature-hover"></div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default LandingPage;
